mapper.select:
SELECT * FROM ( SELECT L.*, ROWNUM R FROM ( SELECT L.lecture_code, L.week_code, L.lecture_reg_date, L.lecture_start_date, L.lecture_end_date, L.lecture_att_time, L.lecture_content, L.video_code, L.lecture_title, L.course_code, V.video_name, V.video_file_path FROM lecture L JOIN video V ON L.video_code = V.video_code
AND L.week_code IN (SELECT week_code FROM course_weekly WHERE course_code = #{course.courseCode})
AND L.lecture_code = #{lecture.lectureCode}
ORDER BY L.week_code DESC) L
WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE R > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT COUNT(*) FROM ( SELECT L.lecture_code, L.week_code, L.lecture_reg_date, L.lecture_start_date, L.lecture_end_date, L.lecture_att_time, L.lecture_content, L.video_code, L.lecture_title, L.course_code, V.video_name, V.video_file_path FROM lecture L JOIN video V ON L.video_code = V.video_code
AND L.week_code IN (SELECT week_code FROM course_weekly WHERE course_code = #{courseCode})
)
