mapper.select:
select count(*) 
from (
	select *
	from professor p
	join department d
	on p.depart_code = d.depart_code
	)



			professor_name = #{keyword}	or depart_name = #{type}


			professor_name = #{keyword}	or depart_name = #{type}


			professor_name = #{keyword}	and depart_name = #{type}

select * from ( select ROWNUM r, professor_id, professor_name, professor_birth, professor_addr, professor_daddr, professor_postal_code, professor_phone, professor_email, professor_img, depart_name from( select p.professor_id, p.professor_name, p.professor_birth, p.professor_addr, p.professor_daddr, p.professor_postal_code, p.professor_phone, p.professor_email, p.professor_img, d.depart_name from professor p join department d on p.depart_code = d.depart_code order by professor_id
professor_name = #{keyword} or depart_name = #{type}
professor_name = #{keyword} or depart_name = #{type}
professor_name = #{keyword} and depart_name = #{type}
) )
WHERE R BETWEEN (#{pageNum}) * #{amount} -9 AND #{pageNum} * #{amount}
