mapper.select:
SELECT REPORT_FILE_PATH,
				REPORT_FILE_NAME
FROM REPORT_FILE
WHERE
REPORT_FILE_CODE = #{reportFileCode}
AND USER_CODE = #{userCode}

SELECT * FROM ( SELECT RF.*, ROWNUM R FROM ( SELECT R.REPORT_FILE_CODE, R.REPORT_FILE_PATH, R.REPORT_FILE_NAME, R.USER_CODE, R.SUBMIT_ID, RE.COURSE_CODE, C.COURSE_YEAR, C.COURSE_SEMESTER, C.COURSE_NAME FROM REPORT_FILE R JOIN REPORT_INFO RE ON R.REPORT_FILE_CODE = RE.REPORT_FILE_CODE JOIN COURSE C ON RE.COURSE_CODE = C.COURSE_CODE WHERE SUBMIT_ID = #{file.submitId} AND USER_CODE = #{file.userCode} ORDER BY C.COURSE_YEAR, C.COURSE_SEMESTER DESC ) RF WHERE
ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE R > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT * FROM ( SELECT A.*, ROWNUM R FROM ( SELECT I.REPORT_CODE, I.WEEK_CODE, I.REPORT_REG_DATE, I.REPORT_START_DATE, I.REPORT_END_DATE, I.REPORT_TITLE, I.REPORT_CONTENT, I.REPORT_ASSIGNED_SCORE, I.REPORT_FILE_CODE, I.COURSE_CODE, F.REPORT_FILE_NAME, F.REPORT_FILE_PATH, F.USER_CODE, F.SUBMIT_ID FROM REPORT_INFO I JOIN REPORT_FILE F ON I.REPORT_FILE_CODE = F.REPORT_FILE_CODE
AND I.COURSE_CODE = #{course.courseCode}
AND I.REPORT_CODE = #{report.reportCode}
AND ${cri.type} = #{cri.keyword}
ORDER BY I.WEEK_CODE DESC) A
WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE R > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT COUNT(*)
FROM REPORT_INFO
WHERE COURSE_CODE = #{course.courseCode}


		AND ${cri.type} = #{cri.keyword}

SELECT M.STUDENT_ID, S.STUDENT_NAME
FROM MY_COURSE M JOIN
STUDENT_PRI_INFO S
ON M.STUDENT_ID = S.STUDENT_ID


		AND M.COURSE_CODE = #{report.courseCode}


		AND M.STUDENT_ID = #{submission.studentId}

SELECT * FROM( SELECT A.*, ROWNUM R FROM (SELECT REPORT_SUBMISSION_CODE, REPORT_CODE, REPORT_SUBMISSION_DATE, REPORT_SCORE, STUDENT_ID, REPORT_SUBMISSION_OPTION, REPORT_FILE_CODE FROM REPORT_SUBMISSION
WHERE REPORT_CODE IN
#{item.reportCode}
ORDER BY REPORT_CODE DESC, STUDENT_ID DESC) A
WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE R > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT COUNT(*) FROM ( SELECT REPORT_SUBMISSION_CODE, REPORT_CODE, REPORT_SUBMISSION_DATE, REPORT_SCORE, STUDENT_ID, REPORT_SUBMISSION_OPTION, REPORT_FILE_CODE FROM REPORT_SUBMISSION
AND REPORT_CODE IN
#{item.reportCode}
AND ${cri.type} = #{cri.keyword}
)

mapper.update:
UPDATE REPORT_SUBMISSION
SET REPORT_SCORE = #{reportScore}
WHERE REPORT_SUBMISSION_CODE = #{reportSubmissionCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM OBJECTION_NO FROM ( SELECT SPI.STUDENT_ID , SPI.STUDENT_NAME , RI.REPORT_TITLE, CW.WEEK_NUM, RO.REPORT_SCORE_OBJECTION_APPROVAL_OPTION, RS.REPORT_SUBMISSION_CODE FROM REPORT_SCORE_OBJECTION RO JOIN STUDENT_STUDY_INFO SI ON RO.STUDENT_ID = SI.STUDENT_ID JOIN STUDENT_PRI_INFO SPI ON SPI.STUDENT_ID = SI.STUDENT_ID JOIN REPORT_SUBMISSION RS ON RS.REPORT_SUBMISSION_CODE = RO.REPORT_SUBMISSION_CODE JOIN REPORT_INFO RI ON RI.REPORT_CODE = RS.REPORT_CODE JOIN COURSE_WEEKLY CW ON RI.WEEK_CODE = CW.WEEK_CODE WHERE RI.COURSE_CODE = #{reportObjection.courseCode}
AND CW.WEEK_NUM = #{cri.type}
ORDER BY 4 DESC) a )
AND objection_no BETWEEN (#{cri.pageNum} - 1) * #{cri.amount} AND #{cri.pageNum} * #{cri.amount}

mapper.update:
update
report_score_objection
set REPORT_SCORE_objection_approval_option = 'A02'
, report_score_objection_rejection_reason =
#{reportScoreObjectionRejectionReason}
where report_submission_code = #{reportSubmissionCode}

update report_submission
set report_score = #{reportScore}
where report_submission_code = #{reportSubmissionCode}
