mapper.select:
SELECT COUNT(*) FROM ( SELECT R.REPORT_CODE, R.WEEK_CODE, R.REPORT_TITLE, R.REPORT_START_DATE, R.REPORT_END_DATE, S.STUDENT_ID, S.REPORT_SCORE, S.REPORT_SUBMISSION_OPTION, R.REPORT_FILE_CODE, S.REPORT_SUBMISSION_CODE, F.REPORT_FILE_NAME, F.REPORT_FILE_PATH, O.REPORT_SCORE_OBJECTION_APPROVAL_OPTION, W.WEEK_NUM FROM REPORT_INFO R JOIN REPORT_SUBMISSION S ON R.REPORT_CODE = S.REPORT_CODE JOIN REPORT_FILE F ON F.REPORT_FILE_CODE = R.REPORT_FILE_CODE LEFT OUTER JOIN REPORT_SCORE_OBJECTION O ON O.REPORT_SUBMISSION_CODE = S.REPORT_SUBMISSION_CODE JOIN COURSE_WEEKLY W ON W.WEEK_CODE = R.WEEK_CODE WHERE S.STUDENT_ID = #{studentId}
AND w.week_num = #{type}
)

select * from ( select a.* , ROWNUM report_no FROM (select r.report_code, r.week_code, r.report_title, r.report_start_date, r.report_end_date, s.student_id, s.report_score, s.report_submission_option, r.report_file_code, s.report_submission_code, f.report_file_name, f.report_file_path, o.report_score_objection_approval_option, w.week_num
from report_info r join report_submission s on r.report_code = s.report_code join report_file f on f.report_file_code = r.report_file_code left outer join report_score_objection o on o.report_submission_code = s.report_submission_code join course_weekly w on w.week_code = r.week_code
AND s.student_id = #{courseInfo.studentId}
AND r.course_code = #{courseInfo.courseCode}
AND r.report_code = #{report.reportCode}
AND w.week_num = #{courseInfo.type}
ORDER BY 14 DESC) a
where ROWNUM <= #{cri.pageNum} * #{cri.amount} ) WHERE report_no > (#{cri.pageNum} - 1) * #{cri.amount}

mapper.update:
UPDATE REPORT_SUBMISSION
SET REPORT_SUBMISSION_OPTION = 'SUB',
REPORT_SUBMISSION_DATE = SYSDATE,
REPORT_FILE_CODE = #{reportFileCode}
WHERE STUDENT_ID = #{studentId} and report_code = #{reportCode}

	    update report_submission
        set report_file_code = (select max(Report_file_code) from report_file)
        where report_code = #{reportCode} and student_id = #{studentId}

mapper.select:
select * from report_submission
where student_id = #{studentId} and REPORT_CODE = #{reportCode}

	and report_submission_option = 'SUB'
