mapper.select:
SELECT * FROM COURSE_NOTICE WHERE COURSE_CODE = #{courseCode}
AND ${searchType} like '%'|| #{search} || '%'
ORDER BY 1 DESC

SELECT *
  FROM COURSE_NOTICE
 WHERE COURSE_CODE = #{courseCode} 
   AND COURSE_NOTICE_NO = #{courseNoticeNo}

SELECT * FROM COURSE_NOTICE WHERE COURSE_CODE = #{courseCode}
AND ${searchType} like '%'|| #{search} || '%'
ORDER BY 1 DESC

SELECT *
  FROM COURSE_NOTICE
 WHERE COURSE_CODE = #{courseCode} 
   AND COURSE_NOTICE_NO = #{courseNoticeNo}

mapper.update:
UPDATE COURSE_NOTICE
SET COURSE_NOTICE_HIT = COURSE_NOTICE_HIT + 1
WHERE COURSE_NOTICE_NO = #{courseNoticeNo}
