mapper.select:
select t.*, find_code(course_sortation) as course_sortation_name, find_code(submit_yes) as submit_yes_name, find_code(okay_yes) as okay_yes_name from temp_course t
where professor_id = #{vo.professorId}
and ${cri.type} = ${cri.keyword}
order by course_code desc

mapper.insert:
select SUBSTR(CONCAT(c.college_code, d.depart_code), 1, 4)||LPAD(TO_CHAR(seq_en.nextval), 4, '0') from college c join department d on c.college_code = d.college_code where d.depart_code = #{departCode}
insert into temp_course( course_name, course_code, course_sortation, course_credit, report_assigned_score, att_assigned_score, midterm_assigned_score, final_assigned_score, course_limit, textbook_reference, course_summary, course_notes, depart_code, course_year, course_semester, professor_id
) values( #{courseName}, #{courseCode}, #{courseSortation}, #{courseCredit}, #{reportAssignedScore}, #{attAssignedScore}, #{midtermAssignedScore}, #{finalAssignedScore}, #{courseLimit}, #{textbookReference}, #{courseSummary}, #{courseNotes}, #{departCode}, #{courseYear}, #{courseSemester}, #{professorId} )

mapper.update:



update temp_course_weekly
set week_content = #{weekContent}
where week_code = #{courseCode}||LPAD(TO_CHAR(#{weekNum}), 2, '0') and course_code = #{courseCode}


update temp_course
set submit_yes = #{submitYes}, okay_yes = #{okayYes}, back_reason = #{backReason}
where course_code = #{courseCode}


 update temp_course
set back_reason = #{backReason}, okay_yes = #{okayYes}, submit_yes=#{submitYes}
where course_code = #{courseCode}


update temp_course
	set back_reason = #{backReason}, okay_yes = #{okayYes}, submit_yes=#{submitYes}
	where course_code = #{courseCode}
